package chap2;

public class BinaryTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10; 		//십진수 : 0 ~ 10
		int bNum = 0B1010;  // 비트
		int oNum = 012; 	//8진수
		int hNum = 0XA5; 	// 16진수(0~9, A~F)
		System.out.println(num);
		System.out.println(bNum + "\n");
		System.out.println(oNum);
		System.out.println(hNum);
		
	 //기초 자료형(primitive 타입)은 null 할당 불가 참조 자료형만 값을 선언하지 않을 때 null
		
	 
	}

}
